mapper.update:
update HUDH_DZBL_DETAIL set blfl=#{blfl,jdbcType=VARCHAR}, bc=#{bc,jdbcType=VARCHAR}, sstype=#{sstype,jdbcType=VARCHAR},detail=#{detail,jdbcType=VARCHAR},createtime=#{createtime,jdbcType=VARCHAR},
status=#{status,jdbcType=VARCHAR},creator=#{creator,jdbcType=VARCHAR} 
where id=#{id,jdbcType=VARCHAR}

mapper.select:
select 
id,title,blfl,bc,detail,createtime,status,blcode,creator,sstype
from HUDH_DZBL_DETAIL where blcode in (${_parameter})

SELECT a.id, a.title, a.blfl, a.bc, a.createtime , a.blcode, u.username AS username, a.creator, p.user_name as createname, a.sstype FROM HUDH_DZBL_DETAIL a LEFT JOIN KQDS_USERDOCUMENT u ON u.usercode = a.blcode LEFT JOIN sys_person p ON p.SEQ_ID = a.creator WHERE 1=1
AND a.createtime >= #{starttime}
AND a.createtime <= #{endtime}
AND (a.blcode LIKE '%${searchValue}%' OR a.sstype LIKE '%${searchValue}%' OR u.username LIKE '%${searchValue}%')
AND a.creator IN(${querytype}) ORDER BY a.createtime desc
