mapper.select:
SELECT a.amount, a.batchnum, a.company, a.createdate, a.createtime, a.createuser, a.goodscode, a.goodsname, a.goodsnorms, a.goodsunit, a.deptpartname, a.housename, a.incode, a.nuitPrice, a.nums, a.organization, a.productionPlace, a.quantity, a.regisnum, a.remark, a.SEQ_ID, a.validity, a.userdocument, a.goodsuuid, b.USER_NAME FROM HUDH_GOOGS_DOCTOR_PICK_IN_DETAIL a LEFT JOIN SYS_PERSON b ON a.createuser = b.seq_id where 1=1
and a.incode = #{incode,jdbcType=VARCHAR}
and a.createuser in (${querytype})

mapper.insert:
