mapper.select:
SELECT kc.id, kc.deptpart, cd.DEPTNAME AS deptpartname, d.goodscode, d.goodsname, h.SEQ_ID AS house, h.housename AS housename, d.goodsnorms, d.goodsunit, kc.nums, kc.goodsdetailid, kc.validity, kc.inremark, kc.organization, kc.goodsprice, kc.createtime, p.USER_NAME FROM HUDH_KSLL_GOODS kc LEFT JOIN KQDS_CK_DEPT cd ON cd.SEQ_ID = kc.deptpart LEFT JOIN KQDS_CK_GOODS_DETAIL d ON d.SEQ_ID = kc.goodsdetailid LEFT JOIN KQDS_CK_GOODS g ON g.goodsdetailid = d.SEQ_ID LEFT JOIN KQDS_CK_HOUSE h ON h.SEQ_ID = g.sshouse LEFT JOIN sys_person p ON p.SEQ_ID = kc.createuser where 1=1
and kc.deptpart = #{deptpart}
AND h.SEQ_ID = #{house}
and d.goodscode = #{goodscode}
and d.goodsname = #{goodsname}
and kc.organization = #{organization}
and (cd.deptName like '%' + #{queryInput} + '%' or d.goodsname like '%' + #{queryInput} + '%' or d.goodsnorms like '%' + #{queryInput} + '%' or d.goodscode like '%' + #{queryInput} + '%')
and d.goodscode in (select goodscode from HUDH_KSLL_COLLOR_DETAIL where parentid = #{parentid,jdbcType=VARCHAR} and status!=3)
order by d.goodscode asc

	select  
	from HUDH_KSLL_GOODS where 
      deptpart = #{deptpart,jdbcType=VARCHAR}
      and goodscode = #{goodscode,jdbcType=VARCHAR}
      
      and house = #{house,jdbcType=VARCHAR}

		select  
		from HUDH_KSLL_GOODS where 1=1
		
      and deptpart = #{deptpart,jdbcType=VARCHAR}
		
      
      and goodscode in (${goodscode})
      
      
      and house = #{house,jdbcType=VARCHAR}
