mapper.select:
SELECT cr.SEQ_ID, cr.lcljId, cg.flowname AS flowname, cr.nodeId, cr.nodeName, cr.nodetime, cr.nexttime, cr.regtime, cr.recesort, cr.regsort, sd.dict_name AS recesortname, d.dict_name AS regsortname, cr.regdoctor, p.USER_NAME AS regdoctorname, cr.remark, yp.USER_NAME AS updateuser, cr.updatetime, sp.USER_NAME AS createuser, cr.createtime, cr.status, ok.blcode, u.username, cr.nodeLimit FROM HUDH_LCLJ_CHECKRECORD cr LEFT JOIN sys_dict d ON d.SEQ_ID = cr.regsort LEFT JOIN sys_dict sd ON sd.SEQ_ID = cr.recesort LEFT JOIN sys_person p ON p.SEQ_ID = cr.regdoctor LEFT JOIN sys_person sp ON sp.SEQ_ID = cr.createuser LEFT JOIN sys_person yp ON yp.SEQ_ID = cr.updateuser LEFT JOIN HUDH_LCLJ_ORDERTRACK ok ON ok.id = cr.lcljId LEFT JOIN HUDH_LCLJ_FLOW_CONFIG cg ON cg.flowcode = ok.flowcode left join kqds_userdocument u on u.seq_id = ok.userdocument_id WHERE 1=1
AND cr.lcljId = #{LcljId}
AND cr.status = #{status}
order by cr.nodetime DESC

SELECT cr.SEQ_ID, cr.lcljId, cg.flowname AS flowname, cr.nodeId, cr.nodeName, cr.nodetime, cr.nexttime, cr.regtime, cr.recesort, cr.regsort, sd.dict_name AS recesortname, d.dict_name AS regsortname, cr.regdoctor, p.USER_NAME AS regdoctorname, cr.remark, yp.USER_NAME AS updateuser, cr.updatetime, sp.USER_NAME AS createuser, cr.createtime, cr.status, ok.blcode, u.username, cr.nodeLimit FROM HUDH_LCLJ_CHECKRECORD cr LEFT JOIN sys_dict d ON d.SEQ_ID = cr.regsort LEFT JOIN sys_dict sd ON sd.SEQ_ID = cr.recesort LEFT JOIN sys_person p ON p.SEQ_ID = cr.regdoctor LEFT JOIN sys_person sp ON sp.SEQ_ID = cr.createuser LEFT JOIN sys_person yp ON yp.SEQ_ID = cr.updateuser LEFT JOIN HUDH_LCLJ_ORDERTRACK ok ON ok.id = cr.lcljId LEFT JOIN HUDH_LCLJ_FLOW_CONFIG cg ON cg.flowcode = ok.flowcode left join kqds_userdocument u on u.seq_id = ok.userdocument_id WHERE 1=1
AND cr.lcljId = #{LcljId}
AND cr.status = #{status}
AND cr.createuser in (${querytype})
order by cr.nodetime DESC

SELECT cr.SEQ_ID, cr.lcljId, cg.flowname AS flowname, cr.nodeId, cr.nodeName, cr.nodetime, cr.nexttime, cr.regtime, cr.recesort, cr.regsort, sd.dict_name AS recesortname, d.dict_name AS regsortname, cr.regdoctor, p.USER_NAME AS regdoctorname, cr.remark, yp.USER_NAME AS updateuser, cr.updatetime, sp.USER_NAME AS createuser, cr.createtime, cr.status, ok.blcode, u.username, cr.nodeLimit FROM HUDH_LCLJ_CHECKRECORD cr LEFT JOIN sys_dict d ON d.SEQ_ID = cr.regsort LEFT JOIN sys_dict sd ON sd.SEQ_ID = cr.recesort LEFT JOIN sys_person p ON p.SEQ_ID = cr.regdoctor LEFT JOIN sys_person sp ON sp.SEQ_ID = cr.createuser LEFT JOIN sys_person yp ON yp.SEQ_ID = cr.updateuser LEFT JOIN HUDH_LCLJ_ORDERTRACK ok ON ok.id = cr.lcljId LEFT JOIN HUDH_LCLJ_FLOW_CONFIG cg ON cg.flowcode = ok.flowcode left join kqds_userdocument u on u.seq_id = ok.userdocument_id WHERE 1=1
AND cr.lcljId = #{LcljId}
AND cr.status = #{status}
order by cr.nodetime DESC

SELECT COUNT(*) FROM HUDH_LCLJ_CHECKRECORD cr WHERE 1=1
AND cr.createuser = #{createuser}
AND cr.status = #{status}

