mapper.select:
		select
		a.id, a.rktime, a.intype, a.supplier ,a.stocktime, a.incode,a.inremark , a.zhaiyao,a.creator,a.createtime,a.modifiy_id,a.status,b.user_name,c.manu_name,check_status
		from HUDH_YKZZ_DRUGS_IN a 
		left join SYS_PERSON b on a.creator = b.seq_id 
		left join HUDH_YKZZ_MANU c on a.supplier = c.id
		where a.status = 0
		AND a.organization = #{organization}
		
        	and a.id = #{id,jdbcType=VARCHAR}
      	
		
			and a.rktime  >= 
			#{starttime}
		
		
			and a.rktime  <= 
			#{endtime}
		
      	
        	and a.intype = #{intype,jdbcType=VARCHAR}
      	
      	
        	and a.supplier = #{supplier,jdbcType=VARCHAR}
      	
      	
        	and a.incode like '%'+#{incode,jdbcType=VARCHAR}+'%'
      	
      	
        	and a.incode = #{accurateIncode,jdbcType=VARCHAR}
      	
      	
			and a.stocktime  >= 
			#{stockstarttime}
		
		
			and a.stocktime  >= 
			#{stockendtime}
		
		
			and a.check_status = #{checkStatus,jdbcType=INTEGER}

select a.seq_id,a.createuser,a.createtime,a.usercode, convert(varchar,convert(decimal(18,3),a.actualmoney)) as actualmoney,
a.status, convert(varchar,convert(decimal(18,3),a.shouldmoney)) as shouldmoney,
convert(varchar,convert(decimal(18,3),a.voidmoney)) as voidmoney, a.isback, b.username as hzname, c.user_name as createname, a.costno from KQDS_COSTORDER a left join KQDS_UserDocument b on a.usercode = b.usercode left join sys_person c on a.createuser = c.seq_id where isback=0 and isdrugs=1 AND a.organization = #{organization}
and a.id = #{id,jdbcType=VARCHAR}
and a.createtime >= #{starttime}
and a.createtime <= #{endtime}
and b.username like '%'+#{hzname,jdbcType=VARCHAR}+'%'
and a.status = #{status,jdbcType=VARCHAR}
and a.issend = #{issend,jdbcType=VARCHAR}
ORDER BY a.createtime DESC

select
costorder_detail_id as seqid,return_drugs as returndrugs,return_drugs_num as returndrugsnum,return_time as returntime
from  KQDS_COSTORDER_DETAIL_RETURN where costorder_detail_id=#{seqid}

select a.seq_id as seqId,a.itemname,a.itemno,a.unit, convert(varchar,a.unitprice) as unitprice,
a.num, convert(varchar,a.subtotal) as subtotal,
k.return_drugs as returnDrugs, k.return_drugs_num as returnDrugsNum, b.drug_total_num, c.batchno from KQDS_COSTORDER_DETAIL a LEFT JOIN KQDS_COSTORDER_DETAIL_RETURN k on k.costorder_detail_id=a.SEQ_ID left join HUDH_YKZZ_DRUGS b on a.itemno = b.order_no LEFT JOIN HUDH_YKZZ_DRUGS_BATCHNUM_SAVE c ON a.SEQ_ID = c.costOrderDetailId where a.seq_id = #{seqId,jdbcType=VARCHAR}

select createtime
	from HUDH_YKZZ_DRUGS_IN
	WHERE id = #{parentId}
