mapper.insert:
insert into KQDS_CHANGE_JDR (SEQ_ID, createuser, createtime,
oldper, toper, remark,
usercode, username, organization
)
values
(#{seqId,jdbcType=VARCHAR}, #{createuser,jdbcType=VARCHAR}, #{createtime,jdbcType=VARCHAR},
#{oldper,jdbcType=VARCHAR}, #{toper,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR},
#{usercode,jdbcType=VARCHAR}, #{username,jdbcType=VARCHAR}, #{organization,jdbcType=VARCHAR}
)

mapper.select:
select per1.user_name as xgr, g.createtime xgsj, g.seq_id, per2.user_name as oldper, per3.user_name as toper, g.remark, g.usercode, u.username, u.createtime jdsj from KQDS_CHANGE_KEFU g LEFT JOIN SYS_PERSON per1 on per1.SEQ_ID = g.createuser LEFT JOIN SYS_PERSON per2 on per2.SEQ_ID = g.oldper LEFT JOIN SYS_PERSON per3 on per3.SEQ_ID = g.toper where 1=1
and g.createtime >= #{starttime}
and g.createtime <= #{endtime}
and g.oldper = #{oldper}
and g.toper = #{toper}
and g.createuser = #{createuser}
and (g.usercode like '%' + #{queryinput} + '%' or g.username like '%' + #{queryinput} + '%' )
ORDER BY g.CreateTime desc
