select goodsuuid,SUM(d.innum) as nums,convert(varchar,convert(decimal(18,3),sum(d.rkmoney))) as rkmoney from KQDS_CK_GOODS_IN_DETAIL d
LEFT JOIN KQDS_CK_GOODS_IN ckin on ckin.incode = d.incode
where 1=1  

	and ckin.inhouse=#{sshouse}


and ckin.shtime  <=  #{jztime}


and ckin.shtime  >=  #{qstime}

GROUP BY d.goodsuuid HAVING d.goodsuuid= #{goodsdetailid}

SELECT gin.seq_id, gin.incode, gin.intype, gin.intype AS intypecode, gys.suppliername AS suppliername, gin.supplier, ghouse.housename AS housename, gin.inhouse, gin.inremark, gin.zhaiyao, gin.rktime, gin.stocktime, p3.user_name AS createuser, gin.createtime FROM KQDS_CK_GOODS_IN gin LEFT JOIN KQDS_CK_HOUSE ghouse ON ghouse.seq_id = gin.inhouse LEFT JOIN KQDS_CK_SUPPLIER gys ON gys.seq_id = gin.supplier LEFT JOIN SYS_PERSON p3 ON p3.seq_id = gin.createuser where 1=1
and gin.createtime >= #{starttime}
and gin.createtime <= #{endtime}
and gin.stocktime >= #{stock_starttime}
and gin.stocktime <= #{stock_endtime}
and gin.intype = #{intype}
and gin.supplier = #{supplier}
and gin.inhouse = #{inhouse}
and gin.organization = #{organization}
and gin.incode like '%' + #{incode} + '%'
and gin.check_status = #{check_status}
and gin.type = #{type}
order by gin.createtime desc

select count(1) from KQDS_CK_GOODS_IN
where supplier =#{supplier}
and organization = #{organization}

SELECT gin.seq_id, gin.incode, gin.intype, gin.intype AS intypecode, gys.suppliername AS suppliername, gin.supplier, ghouse.housename AS housename, gin.inhouse, gin.inremark, gin.zhaiyao, gin.rktime, gin.stocktime, p3.user_name AS createuser, gin.createtime FROM KQDS_CK_GOODS_IN gin LEFT JOIN KQDS_CK_HOUSE ghouse ON ghouse.seq_id = gin.inhouse LEFT JOIN KQDS_CK_SUPPLIER gys ON gys.seq_id = gin.supplier LEFT JOIN SYS_PERSON p3 ON p3.seq_id = gin.createuser where 1=1
and gin.createtime >= #{starttime}
and gin.createtime <= #{endtime}
and gin.stocktime >= #{stock_starttime}
and gin.stocktime <= #{stock_endtime}
and gin.intype = #{intype}
and gin.supplier = #{supplier}
and gin.inhouse = #{inhouse}
and gin.organization = #{organization}
and gin.incode like '%' + #{incode} + '%'
and gin.check_status = #{check_status}
and gin.status = #{status}
and gin.type = #{type}
order by gin.createtime desc
