mapper.select:
select
*
from KQDS_CK_GOODS_TYPE
where 1=1

	and goodstype like '%' +#{search} + '%'


	and organization = #{organization}


	and perid = #{perid}


	and typeno = #{typeno}

order by sortno

select * from KQDS_CK_GOODS_TYPE where 1=1
and organization = #{organization}
and perid = #{perid}
order by sortno

select
*
from KQDS_CK_GOODS_TYPE
where 1=1 and perid = #{perid}

	and organization = #{organization}

order by sortno

select count(1) from KQDS_CK_GOODS_TYPE where 1=1

	and typeno = #{typeno}


	and perid = #{perid}
