mapper.select:
select s.seq_id, s.suppliercode, s.suppliername, s.sortno, p.user_name as createuser, s.createtime, s.organization from KQDS_CK_SUPPLIER s LEFT JOIN SYS_PERSON p on p.seq_id = s.createuser where 1=1
and s.organization = #{organization}
and s.suppliername = #{suppliername}
and s.suppliercode = #{suppliercode}
order by s.sortno

select count(1) from KQDS_CK_SUPPLIER
where 1=1

	and suppliercode = #{suppliercode}


	and organization = #{organization}


	and seq_Id != #{seqId}
