SELECT detail.y as 年,SUM(detail.paymoney-detail.jcfmoney-detail.yjjmoney) as paymoney,SUM(detail.jcfmoney) jcfmoney,SUM(detail.yjjmoney) yjjmoney FROM ( SELECT year(cost.sftime) y, (p.actualmoney - (SELECT ISNULL(SUM (paymoney - payyjj - payother2 - payintegral - paydjq),0) AS paymoney FROM KQDS_COSTORDER_DETAIL WHERE 1=1 AND isyjjitem=1 AND costno=p.costno) ) paymoney, (SELECT ISNULL(SUM (paymoney - payyjj - payother2 - payintegral - paydjq),0) AS paymoney FROM KQDS_COSTORDER_DETAIL WHERE 1=1 and isyjjitem!=1 AND itemname IN ( SELECT treatitemname FROM KQDS_TREATITEM WHERE basetype = 'jyk671' AND nexttype = 'b87aa149-a257-4b00-8fad-2c9b331af38e') AND costno=p.costno) jcfmoney, ISNULL(SUM (costd.payyjj),0) AS yjjmoney, p.costno c FROM KQDS_REG r LEFT JOIN KQDS_PAYCOST p on r.seq_id=p.regno LEFT JOIN KQDS_COSTORDER cost ON p.costno = cost.costno LEFT JOIN KQDS_COSTORDER_DETAIL costd ON p.costno = costd.costno where 1 = 1 AND r.recesort in (${recesort}) AND YEAR (cost.sftime) >= #{startyear} AND YEAR (cost.sftime) <= #{endyear}
AND p.askperson in (${visualstaff})
AND r.organization = #{organization} AND cost.isdrugs = 0 group by year(cost.sftime), p.actualmoney, p.costno ) as detail group by detail.y ORDER BY detail.y ASC

