mapper.select:
select dept.DEPT_NAME as organizationname, etype.createuser as typecreateuser, t.jhname, per1.user_name as createuser, v.createtime, v.hfresult, v.visittime, v.extension, v.seq_id, v.remark as extenremark, u.usercode, u.username, u.sex, u.age, u.phonenumber1, v.finishtime, v.hfyd, v.status, v.organization, kcit1.dict_name as myd, per2.user_name as visitor, visitor as visitorid, (select count(1) from KQDS_Member where usercode =v.usercode ) as memberno, u.remark from KQDS_Extension v LEFT JOIN KQDS_UserDocument u on u.UserCode = v.usercode LEFT JOIN KQDS_Extension_Type t on v.extension = t.seq_id LEFT JOIN SYS_PERSON per1 on per1.SEQ_ID = u.createuser LEFT JOIN SYS_PERSON per2 on per2.SEQ_ID = v.visitor LEFT JOIN KQDS_EXTENSION_TYPE etype on etype.SEQ_ID = v.extension LEFT JOIN SYS_DICT kcit1 on v.myd = kcit1.seq_id LEFT JOIN SYS_DEPT dept on v.organization = dept.dept_code and dept.dept_parent = '0' where 1=1
and v.status = #{status}
and v.visittime >= #{vtime1}
and v.visittime <= #{vtime2}
and t.jhname = like '%' + #{extension} + '%'
and v.organization = #{organization}
and v.visitor = #{visitor}
and t.status = #{planstatus}
and v.extension = #{tuiguang}
and (v.createuser in (${visualstaff}) or v.visitor in (${visualstaff})) ORDER BY v.status asc,v.createtime desc

select COUNT(extension) as flcount,jhname,extension from KQDS_Extension e
LEFT JOIN KQDS_EXTENSION_TYPE t on e.extension = t.seq_id where 1=1

	and e.status = #{status}


	and e.visittime  >= 
	#{vtime1}


	and e.visittime  <= 
	#{vtime2}


	and t.jhname = like '%' + #{extension} + '%'


	and e.organization = #{organization}


	and e.visitor = #{visitor}


	and t.status = #{planstatus}


	and e.extension = #{tuiguang}

and (e.createuser in (${visualstaff}) or e.visitor in (${visualstaff}))
GROUP BY jhname,extension


select count(1) from KQDS_Extension e where 1=1

	and e.status = #{status}


	and e.visittime  >= 
	#{vtime1}


	and e.visittime  <= 
	#{vtime2}


	and e.extension = like '%' + #{extension} + '%'


	and e.organization = #{organization}


	and e.visitor = #{visitor}


	
		and finishtime  < 
		DATE_ADD(visittime,INTERVAL 1 DAY)
	
	
		and finishtime  < 
		dateadd(day, 1, visittime)
	


	and e.extension = #{tuiguang}

and (createuser in (${visualstaff}) or visitor in (${visualstaff}))
