mapper.insert:
insert into KQDS_JZMD_TYPE (SEQ_ID, createuser, createtime,
jzmd, jzmdchildname, txjzmd,
organization, yxday, useflag
)
values
(#{seqId,jdbcType=VARCHAR}, #{createuser,jdbcType=VARCHAR}, #{createtime,jdbcType=VARCHAR},
#{jzmd,jdbcType=VARCHAR}, #{jzmdchildname,jdbcType=VARCHAR},
#{txjzmd,jdbcType=VARCHAR},
#{organization,jdbcType=VARCHAR}, #{yxday,jdbcType=INTEGER}, #{useflag,jdbcType=INTEGER}
)

mapper.select:
select dict1.dict_name fname, dict1.SEQ_ID as fid, dict2.dict_name cname, dict2.SEQ_ID as cid, jzmd.SEQ_ID as jzmdid, jzmd.txjzmd, dict3.dict_name as txname, jzmd.yxday, jzmd.createtime, p.user_name as createuser from SYS_DICT dict1 LEFT JOIN SYS_DICT dict2 on dict2.parent_code = dict1.dict_code LEFT JOIN KQDS_JZMD_TYPE jzmd on jzmd.jzmdchildname = dict2.SEQ_ID and jzmd.organization = #{organization} LEFT JOIN SYS_DICT dict3 on dict3.SEQ_ID = jzmd.txjzmd LEFT JOIN SYS_PERSON p on jzmd.createuser = p.SEQ_ID where 1=1 and dict1.parent_code ='JZMD' and (dict1.organization = #{organization} or dict1.organization = '')
and dict1.seq_id = #{jzmd}
and dict2.seq_id = #{jzmdchildname}
ORDER BY dict1.orderno

select * from KQDS_JZMD_TYPE where jzmd = #{jzmd}

	and useflag = 0

and organization = #{organization}

select txjzmd,yxday from KQDS_JZMD_TYPE
where jzmd = #{reggoal}
and jzmdchildname = #{jzmd}
and organization =
#{organization}
and yxday > 0
