mapper.select:
select d.dept_name docdept, m.seq_id, m.usercode, p.user_name as createuser, kcit1.dict_name as blfl, kcit2.dict_name as bc, m.mtype, m.createtime, m.status, userd.username, userd.phonenumber1, d.dept_name as regdept, reg.regsort from KQDS_MEDICALRECORD m LEFT JOIN KQDS_REG reg on m.RegNo=reg.SEQ_ID LEFT JOIN SYS_PERSON p on p.SEQ_ID=m.createuser LEFT JOIN KQDS_UserDocument userd on m.usercode=userd.usercode LEFT JOIN SYS_DEPT d on p.DEPT_ID=d.SEQ_ID LEFT JOIN SYS_DICT kcit1 on m.blfl = kcit1.seq_id LEFT JOIN SYS_DICT kcit2 on m.bc = kcit2.seq_id where 1=1
and m.createtime >= #{starttime}
and m.createtime <= #{endtime}
and (userd.usercode like '%' + #{searchvalue} + '%' or userd.username like '%' + #{searchvalue} + '%' or userd.Address like '%' + #{searchvalue} + '%' or ${p1} or ${p2} or userd.pym like '%' + #{searchvalue} + '%' )
and d.SEQ_ID = #{regdept}
and m.usercode = #{usercode}
and m.createuser = #{createuser}
and m.status = ${status}
and m.blfl = #{blfl}
and m.bc = #{bc}
and m.mtype = ${mtype}
and m.createuser in (${visualstaff})
and m.organization = #{organization}
ORDER BY ${sortName} ${sortOrder}
ORDER BY m.CreateTime desc

select * from KQDS_MEDICALRECORD where 1=1
and usercode = #{usercode}
and m.createtime >= #{starttime}
order by createtime asc

select b.*, u.username, dict1.dict_name as blflname, dict2.dict_name as bcname, per1.user_name as createusername, odept.dept_name as organizationname from KQDS_MEDICALRECORD b left join KQDS_USERDOCUMENT u on u.usercode = b.usercode left join SYS_PERSON per1 on per1.seq_id = b.createuser left join SYS_DICT dict1 on dict1.seq_id = b.blfl left join SYS_DICT dict2 on dict2.seq_id = b.bc LEFT JOIN SYS_DEPT odept on odept.dept_code = b.organization and odept.dept_parent = '0' where 1=1 and status = 2 and mtype in (0,1)
and b.usercode = #{usercode}
ORDER BY b.createtime desc

select b.*, u.username, dict1.dict_name as blflname, dict2.dict_name as bcname, per1.user_name as createusername, odept.dept_name as organizationname from KQDS_MEDICALRECORD b left join KQDS_USERDOCUMENT u on u.usercode = b.usercode left join SYS_PERSON per1 on per1.seq_id = b.createuser left join SYS_DICT dict1 on dict1.seq_id = b.blfl left join SYS_DICT dict2 on dict2.seq_id = b.bc LEFT JOIN SYS_DEPT odept on odept.dept_code = b.organization and odept.dept_parent = '0' where 1=1 and status = 2 and mtype in (2,3,4,5)
and b.usercode = #{usercode}
ORDER BY b.createtime desc

select DISTINCT d.regno 
from KQDS_MEDICALRECORD d
LEFT JOIN KQDS_REG r on r.SEQ_ID=d.regno
LEFT JOIN KQDS_RECEIVEINFO i on i.regno = d.regno
LEFT JOIN KQDS_USERDOCUMENT u on u.usercode =d.usercode  
where 1=1 

	and i.dev_item = #{devItem}


	and r.recesort = #{recesort}


	and r.askperson = #{askperson}


	and r.regdept = #{regdept}


	and r.doctor = #{doctor}


	and r.createtime  >= 
	#{starttime}


	and r.createtime  <= 
	#{endtime}


	and r.regsort = #{regsort}


	and r.cjstatus = #{cjstatus}


	and kcit7.SEQ_ID =#{gongju} 


	and r.ifmedrecord =#{ifmedrecord}


	and (u.usercode like '%' + #{searchValue} + '%'
	or u.username like '%' + #{searchValue} + '%'
	or ${p1}
	or ${p2}
	or u.pym like '%' + #{searchValue} + '%'
	)


and u.askperson =#{firstaskperson}


and u.usercode =#{usercode}


and u.seq_id =#{useqid}


	and u.important = #{important}


	and u.devchannel = #{devchannel}


	and u.nexttype = #{nexttype}


	
		and u.age  >= 
		'0' and u.age  <= 
		'10'
	
	
		and u.age  >= 
		'10' and u.age  <= 
		'20'
	
	
		and u.age  >= 
		'20' and u.age  <= 
		'30'
	
	
		and u.age  >= 
		'30' and u.age  <= 
		'40'
	
	
		and u.age  >= 
		'40' and u.age  <= 
		'50'
	
	
		and u.age  >= 
		'50'
	


	and r.organization = #{organization}


	and d.createuser in(${visualstaff})
