mapper.select:
select p2.USER_NAME as askperson, r.cardno, r.organization, r.sqstatus, r.sqremark, r.xjmoney, r.yhkmoney, r.qtmoney, r.zfbmoney, r.wxmoney, r.mmdmoney, r.bdfqmoney, r.seq_id, p.user_name as createuser, r.createtime, r.usercode, r.username, r.cardno, r.type, r.cmoney, r.cgivemoney, r.ctotal, r.ymoney, r.ygivemoney, r.ytotal, r.tfremark, u.createuser as jdrid, p3.user_name as jdrname, kcit3.dict_name as devchannel, hzly.dict_name as nexttype from KQDS_MEMBER_RECORD_SH r LEFT JOIN SYS_PERSON p on r.createuser = p.seq_id LEFT JOIN KQDS_UserDocument u on u.usercode = r.usercode LEFT JOIN SYS_PERSON p2 on u.AskPerson = p2.seq_id LEFT JOIN SYS_PERSON p3 on u.createuser = p3.seq_id LEFT JOIN SYS_DICT kcit3 on u.devchannel = kcit3.seq_id LEFT JOIN SYS_DICT hzly on u.nexttype = hzly.seq_id where 1=1
and (r.usercode like '%' + #{queryinput} + '%' or u.username like '%' + #{queryinput} + '%' or u.pym like '%' + #{queryinput} + '%' or r.cardno like '%' + #{queryinput} + '%' )
and r.type = #{type}
and r.createtime >= #{starttime}
and r.createtime <= #{endtime}
and r.sqstatus = #{sqstatus}
and r.organization = #{organization}
order by r.createtime
