mapper.select:
select p1.user_id as personidname, p2.user_id as createusername, p.seq_id, p.personid, p.startdate, p.enddate, p.orderstatus, p.remark, p.createtime, p.createuser, p.deptid from KQDS_PAIBAN p LEFT JOIN KQDS_PAIBAN_TYPE t on t.typename = p.orderstatus LEFT JOIN SYS_PERSON p1 on p1.seq_id = p.personid LEFT JOIN SYS_PERSON p2 on p2.seq_id = p.createuser where 1=1 and p.organization = #{organization}
and p.personid = #{personid}
and p.startdate >= #{starttime}
and p.enddate <= #{endtime}
and p.orderstatus = #{orderstatus}
and p.regdept = #{regdept}
order by p.createtime desc

select count(1) num from KQDS_Paiban
where 1=1
and organization =#{organization}

	and startdate like '%' + #{starttime} + '%'


	and personid = #{personid}

select seq_id from KQDS_Paiban
where 1=1
and organization =#{organization}

	and startdate like '%' + #{starttime} + '%'


	and personid = #{personid}
