mapper.select:
select t.seq_id, t.usercode, t.costno, t.username, t.status, t.tkze, t.createtime, t.createuser, per1.user_name as createusername, t.shouldmoney, t.arrearmoney, t.actualmoney, t.arrearmoney from KQDS_REFUND t LEFT JOIN SYS_PERSON per1 on per1.SEQ_ID = t.createuser left join KQDS_UserDocument u on u.usercode = t.usercode where 1=1 and t.organization = #{organization}
and ( t.usercode like '%' + #{searchValue} + '%' or t.pym like '%' + #{searchValue} + '%' or t.username like '%' + #{searchValue} + '%' or t.seq_id like '%' + #{searchValue} + '%' )
and t.createtime >= #{starttime}
and t.createtime <= #{endtime}
and t.createtime = #{status}
and ( t.createuser in (${visualstaff}) or t.doctor in (${visualstaff}) )
ORDER BY t.CreateTime desc
