mapper.select:
select rt.*,d.payother2 as zsmoney,d.paymoney-d.payother2 as ssmoney from KQDS_REFUND_DETAIL rt left join KQDS_COSTORDER_DETAIL d on d.seq_id = rt.orderdetailno where 1=1
and rt.refundid = #{refundid}
ORDER BY rt.CreateTime desc

select rt.SEQ_ID,rt.itemname,rt.tknum,rt.tkmoney,rt.remark,d.payxj,d.payyjj,d.paybank,d.payyb,d.payother1,d.payother2,d.payzfb,d.paywx,d.paymmd,d.paybdfq,d.paydjq,d.payintegral from KQDS_REFUND_DETAIL rt left join KQDS_COSTORDER_DETAIL d on d.seq_id = rt.orderdetailno where 1=1
and rt.refundid = #{refundid}
ORDER BY rt.CreateTime desc
