mapper.select:
select count(1) from KQDS_Room where 1=1
and doctor = #{doctor}
and nurse = #{nurse}
and roomid = #{roomid}
and usercode = #{usercode}
and seq_id != #{seqId}
and ( (startTime <= #{starttime} AND endTime >= #{endtime}) OR (startTime <= #{starttime} AND endTime < #{endtime} and endTime > #{starttime}) OR (startTime >= #{starttime} AND endTime >= #{endtime} and startTime < #{endtime}) OR (startTime >= #{starttime} AND endTime <= #{endtime} ) )
and isdelete != 1

select * from KQDS_Room where 1=1
and doctor = #{doctor}
and nurse = #{nurse}
and roomid = #{roomid}
and usercode = #{usercode}
and ( (startTime <= #{starttime} AND endTime >= #{endtime}) OR (startTime <= #{starttime} AND endTime < #{endtime} and endTime > #{starttime}) OR (startTime >= #{starttime} AND endTime >= #{endtime} and startTime < #{endtime}) OR (startTime >= #{starttime} AND endTime <= #{endtime} ) )
and isdelete != 1

select * from KQDS_HOSPITAL_ORDER where 1=1
and askperson = #{doctor}
and askperson = #{nurse}
and usercode = #{usercode}
and ( (startTime <= #{starttime} AND endTime >= #{endtime}) OR (startTime <= #{starttime} AND endTime < #{endtime} and endTime > #{starttime}) OR (startTime >= #{starttime} AND endTime >= #{endtime} and startTime < #{endtime}) OR (startTime >= #{starttime} AND endTime <= #{endtime} ) )
and isdelete != 1

select r.SEQ_ID, r.roomid, kcit1.dict_name as roomname, r.zzxt, kcit2.dict_name as zzxtname, r.ks, r.roomstatus, r.starttime, r.endtime, r.doctor, per1.USER_NAME as doctorname, r.nurse, per2.USER_NAME as nursename, r.askperson, per5.USER_NAME as askpersonname, r.phasedoctor, per6.USER_NAME as phasedoctorname, r.usercode, u.username as username, r.remark, r.createtime, r.createuser, per3.USER_NAME as createusername, r.isdelete, r.delreason, r.delperson, per4.USER_NAME as delpersonname, r.organization, r.tooth, u.sex, u.age, u.phonenumber1, u.phonenumber2 from KQDS_ROOM r LEFT JOIN SYS_DICT kcit1 on r.roomid = kcit1.seq_id LEFT JOIN SYS_DICT kcit2 on r.zzxt = kcit2.seq_id LEFT JOIN SYS_PERSON per1 on per1.SEQ_ID = r.doctor LEFT JOIN SYS_PERSON per2 on per2.SEQ_ID = r.nurse LEFT JOIN SYS_PERSON per3 on per3.SEQ_ID = r.createuser LEFT JOIN SYS_PERSON per4 on per4.SEQ_ID = r.delperson LEFT JOIN SYS_PERSON per5 on per5.SEQ_ID = r.askperson LEFT JOIN SYS_PERSON per6 on per6.SEQ_ID = r.phasedoctor LEFT JOIN KQDS_UserDocument u on u.usercode = r.usercode where 1=1 and r.isdelete != 1
and r.starttime >= #{starttime}
and r.endtime <= #{endtime}
and roomid = #{roomid}
and r.doctor = #{doctor}
and r.nurse = #{nurse}
and r.zzxt = #{zzxt}
and r.organization = #{organization}
order by r.createtime desc

select r.seq_id, r.roomid, kcit1.dict_name as roomname, r.starttime, r.endtime, r.doctor, per1.user_name as doctorname, r.nurse, per2.user_name as nursename, r.usercode, u.username, r.remark, r.createtime, r.createuser, per5.user_name as createusername, r.organization, r.isdelete, r.delreason, r.delperson, per3.user_name as delpersonname, r.zzxt, kcit2.dict_name as zzxtname, r.ks, r.roomstatus, u.PhoneNumber1, u.familyship, u.createuser as jd, per4.user_name as jdr, u.createtime jdsj from KQDS_ROOM r LEFT JOIN KQDS_UserDocument u on u.usercode = r.usercode LEFT JOIN SYS_PERSON per1 on per1.SEQ_ID = r.doctor LEFT JOIN SYS_PERSON per2 on per2.SEQ_ID = r.nurse LEFT JOIN SYS_PERSON per5 on per5.SEQ_ID = r.createuser LEFT JOIN SYS_PERSON per3 on per3.SEQ_ID = r.delperson LEFT JOIN SYS_PERSON per4 on per4.SEQ_ID = u.createuser LEFT JOIN SYS_DICT kcit1 on r.roomid = kcit1.seq_id LEFT JOIN SYS_DICT kcit2 on r.zzxt = kcit2.seq_id where 1=1
and r.starttime >= #{starttime}
and r.endtime <= #{endtime}
and r.zzxt = #{zzxt}
and u.username like '%' + #{name} + '%'
and (u.usercode like '%' + #{username} + '%' or u.username like '%' + #{username} + '%' or ${p1} or ${p2} or u.pym like '%' + #{username} + '%' )
and r.organization = #{organization}
and r.roomstatus = #{roomstatus}
and r.doctor = #{doctor}
and r.nurse = #{nurse}
and r.isdelete != '1'
and r.isdelete != '1' and (r.doctor in (${visualstaff}) or r.nurse in (${visualstaff}) or r.createuser in (${visualstaff}))
AND (u.username LIKE '%' + #{searchValue} + '%' OR u.PhoneNumber1 LIKE '%' + #{searchValue} + '%' OR u.PhoneNumber2 LIKE '%' + #{searchValue} + '%')
ORDER BY ${sortName} ${sortOrder}
ORDER BY r.CreateTime desc
