mapper.select:
select b.*, u.usercode, u.username, u.phonenumber1, u.phonenumber2 from KQDS_SOUND_RECORD b left join KQDS_USERDOCUMENT u on u.phonenumber1 = b.phonenumber where 1=1
and b.recordtime >= #{starttime}
and b.recordtime <= #{endtime}
and b.phonenumber = #{phonenumber}
ORDER BY b.recordtime desc

select count(1) from KQDS_SOUND_RECORD
where linenum = #{linenum}
and phonenumber = #{phonenumber}
and
filename = #{filename}
and recordtime = #{recordtime}
