mapper.select:
select t.seq_id, t.toothseat, t.toothtype, kcit1.dict_name as toothtypename, t.yw1, t.yw2, t.yw3, t.yw4, t.detaildesc, t.usertype, t.diseasesort, t.createuser, per1.user_name as createusername, t.createtime, t.remark from KQDS_TOOTH_DOC t LEFT JOIN SYS_DICT kcit1 on t.toothtype = kcit1.seq_id LEFT JOIN SYS_PERSON per1 on per1.SEQ_ID = t.createuser where 1=1
and t.regno = #{regno}
and t.usercode = #{usercode}
ORDER BY t.CreateTime desc

select hz.seq_id, hz.createtime, hz.createuser, per1.USER_NAME as createusername, hz.usercode, hz.diseasesort, hz.toothseat, hz.remark, hz.detaildesc, hz.toothtype, kcit1.dict_name as toothtypename, hz.yw1, hz.yw2, hz.yw3, hz.yw4, hz.usertype, odept.dept_name as organizationname, u.username, kcit3.dict_name as devchannel, hzly.dict_name as nexttype, per10.USER_NAME as faskperson, per3.USER_NAME as jdr from KQDS_TOOTH_DOC hz LEFT JOIN KQDS_UserDocument u on hz.usercode = u.UserCode LEFT JOIN SYS_PERSON per1 on per1.SEQ_ID = hz.createuser LEFT JOIN SYS_PERSON per3 on per3.SEQ_ID = u.CreateUser LEFT JOIN SYS_PERSON per10 on per10.SEQ_ID = u.askperson LEFT JOIN SYS_DICT kcit1 on hz.toothtype = kcit1.seq_id LEFT JOIN SYS_DICT kcit3 on u.devchannel = kcit3.seq_id LEFT JOIN SYS_DICT hzly on u.nexttype = hzly.seq_id LEFT JOIN SYS_DEPT odept on odept.dept_code = hz.organization and odept.dept_parent = '0' where 1=1
and hz.organization = #{organization}
and hz.createtime >= #{starttime}
and hz.createtime <= #{endtime}
and hz.diseasesort >= #{txstarttime}
and hz.diseasesort <= #{txendtime}
and hz.toothseat = #{toothseat}
and hz.toothtype = #{toothtype}
and (u.usercode like '%' + #{queryinput} + '%' or u.username like '%' + #{queryinput} + '%' or u.pym like '%' + #{queryinput} + '%' )
and hz.createuser = #{createuser}
and ( hz.createuser in (${visualstaff}) or u.doctor in (${visualstaff}) or u.askperson in (${visualstaff}) or u.createuser in (${visualstaff}) )
ORDER BY hz.createtime desc
