mapper.select:
select t.seq_id, odept.dept_name as organizationname, t.organization, t.status, t.visittime, t.visitor, per1.user_name as visitorname, t.usercode, t.telphone, t.sex, t.username, t.hffl, kcit1.dict_name as hfflname, t.hfyd, t.postperson, per2.user_name as postpersonname, t.hfresult, t.finishtime, t.myd, t.accepttype, t.visittype, kcit4.dict_name as accepttypename, kcit2.dict_name as mydname from KQDS_VISIT t LEFT JOIN SYS_DEPT odept on odept.dept_code = t.organization and odept.dept_parent = '0' LEFT JOIN SYS_PERSON per1 on per1.SEQ_ID = t.visitor LEFT JOIN SYS_PERSON per2 on per2.SEQ_ID = t.postperson LEFT JOIN SYS_DICT kcit1 on t.hffl = kcit1.seq_id LEFT JOIN SYS_DICT kcit2 on t.myd = kcit2.seq_id LEFT JOIN SYS_DICT kcit4 on t.accepttype = kcit4.seq_id where 1=1
and t.usercode=#{usercode}
and t.organization=#{organization}
and t.myd like '%' + #{myd} + '%'
and t.visittime like '%' + #{visittime} + '%'
and per1.user_name like '%' + #{username} + '%'
and t.hffl = #{hffl}
and t.status = #{status}
ORDER BY ${sortName} ${sortOrder}
ORDER BY t.visittime desc

select t.seq_id, odept.dept_name as organizationname, t.organization, t.status, t.visittime, t.visitor, per1.user_name as visitorname, t.usercode, t.telphone, t.sex, t.username, t.hffl, kcit1.dict_name as hfflname, t.hfyd, t.postperson, per2.user_name as postpersonname, t.hfresult, t.finishtime, t.myd, t.accepttype, t.visittype, kcit4.dict_name as accepttypename, kcit2.dict_name as mydname from KQDS_VISIT t LEFT JOIN SYS_DEPT odept on odept.dept_code = t.organization and odept.dept_parent = '0' LEFT JOIN SYS_PERSON per1 on per1.SEQ_ID = t.visitor LEFT JOIN SYS_PERSON per2 on per2.SEQ_ID = t.postperson LEFT JOIN SYS_DICT kcit1 on t.hffl = kcit1.seq_id LEFT JOIN SYS_DICT kcit2 on t.myd = kcit2.seq_id LEFT JOIN SYS_DICT kcit4 on t.accepttype = kcit4.seq_id where 1=1
and t.usercode=#{usercode}
and t.organization=#{organization}
and t.myd like '%' + #{myd} + '%'
and t.visittime like '%' + #{visittime} + '%'
and t.username like '%' + #{username} + '%'
and t.visitor = #{visitor}
and t.hffl = #{hffl}
and t.status = #{status}
ORDER BY ${sortName} ${sortOrder}
ORDER BY t.visittime desc

select t.seq_id, t.organization, t.status, t.visittime, t.visitor, per1.user_name as visitorname, t.usercode, t.telphone, t.sex, t.username, t.hffl, kcit1.dict_name as hfflname, t.finishtime, t.hfyd, t.postperson, per2.user_name as postpersonname, t.hfresult, t.myd, kcit2.dict_name as mydname from KQDS_VISIT t LEFT JOIN SYS_PERSON per1 on per1.SEQ_ID = t.visitor LEFT JOIN SYS_PERSON per2 on per2.SEQ_ID = t.postperson LEFT JOIN SYS_DICT kcit1 on t.hffl = kcit1.seq_id LEFT JOIN SYS_DICT kcit2 on t.myd = kcit2.seq_id where 1=1 and t.organization = #{organization}
and to_days(t.visittime) = to_days(now())
and datediff(day, t.visittime,getdate())=0
and t.visitor in (${visualstaff})
ORDER BY ${sortName} ${sortOrder}
order by t.visittime desc

select dept.DEPT_NAME as organizationname, v.hfresult, v.visittime, v.seq_id, v.hffl, v.finishtime, v.hfyd, v.status, v.organization, v.visitor, v.usercode, u.remark, u.username, u.sex, u.BirthDay, u.phonenumber1, u.phonenumber2, u.doorstatus, ps2.user_name as jdr,
okdi1.dict_name as myd, okdi2.dict_name as hffl1, (select count(1) from KQDS_Member where usercode =v.usercode ) as memberno, dept2.DEPT_NAME as visitdeptname, ps.user_name as visitor1,
v.visitor as visitorid from KQDS_Visit v LEFT JOIN KQDS_UserDocument u on u.UserCode =v.usercode
LEFT JOIN SYS_DICT okdi1 on okdi1.seq_id = v.myd LEFT JOIN SYS_DICT okdi2 on okdi2.seq_id = v.hffl
LEFT JOIN SYS_DICT okdi1 on okdi1.seq_id = v.myd
LEFT JOIN SYS_PERSON ps on ps.seq_id = v.visitor LEFT JOIN SYS_DEPT dept2 on ps.dept_id = dept2.seq_id LEFT JOIN SYS_PERSON ps2 on ps2.seq_id = u.createuser LEFT JOIN SYS_DEPT dept on v.organization = dept.dept_code and dept.dept_parent = '0' where 1=1 and v.organization = #{organization}
and v.visittime >= #{vtime1}
and v.visittime <= #{vtime2}
and (v.username like '%' + #{name} + '%' or u.pym like '%' + #{name} + '%')
and v.hffl=#{hffl}
and v.myd=#{myd}
and dept2.seq_id=#{visitdept}
and v.status=#{status}
and e.extension=#{renwu}
and v.visitor=#{visitor}
and v.status=#{ishuifang}
and (u.usercode like '%' + #{phonenumber1} + '%' or u.username like '%' + #{phonenumber1} + '%' or ${p1} or ${p2} or u.pym like '%' + #{phonenumber1} + '%' )
and (v.visitor in (${visualstaff}) or v.createuser in (${visualstaff}) or v.postperson in (${visualstaff}) )
ORDER BY ${sortName} ${sortOrder}
ORDER BY v.createtime desc

select
count(v.hffl) as flcount,
v.hffl,
okdi2.dict_name
from KQDS_Visit v
LEFT JOIN KQDS_UserDocument u on u.UserCode = v.usercode
LEFT JOIN SYS_DICT okdi2 on okdi2.seq_id = v.hffl
LEFT JOIN SYS_PERSON ps on ps.seq_id = v.visitor
LEFT JOIN SYS_DEPT dept2 on ps.dept_id = dept2.seq_id
LEFT JOIN KQDS_Extension e on v.usercode = e.usercode
where 1=1
and
v.organization = #{organization}

	and v.visittime  >= 
	#{vtime1}


	and v.visittime  <= 
	#{vtime2}


	and (v.username like '%' + #{name} + '%' or u.pym like '%' + #{name} + '%')


	and v.hffl=#{hffl}


	and v.myd=#{myd}


	and dept2.seq_id=#{visitdept}


	and v.status=#{status}


	and e.extension=#{renwu}


	and v.visitor=#{visitor}


	and v.status=#{ishuifang}


	and (u.usercode like '%' + #{phonenumber1} + '%'
	or u.username like '%' + #{phonenumber1} + '%'
	or ${p1}
	or ${p2}
	or u.pym like '%' + #{phonenumber1} + '%'
	)


	and (v.visitor in (${visualstaff}) or v.createuser in (${visualstaff}) or v.postperson in (${visualstaff}) )

GROUP BY v.hffl ,okdi2.dict_name

