select
select top ${num1} * from (select row_number() over(order by v.createtime desc) as rownumber,
p.user_name as visitorname,kcit1.dict_name as hfflname,kcit2.dict_name as mydname,v.* from KQDS_VISIT v LEFT JOIN SYS_DICT kcit1 on v.hffl = kcit1.seq_id LEFT JOIN SYS_PERSON p on p.SEQ_ID = v.visitor LEFT JOIN SYS_DICT kcit2 on v.myd = kcit2.seq_id where 1=1 and v.visitor in (${visualstaff}) and v.organization = #{organization}
and v.visittime >= #{starttime}
and v.visittime <= #{endtime}
order by v.visittime desc limit ${num1},${num2}
order by v.visittime desc ) as u1 where 1=1 and rownumber > ${num2}

select count(1)
from KQDS_VISIT 
where visittime=#{time} and visitor=#{seqid}
